// Sample GridSimulation subclass for Shader Search 3
class StarterSimulation extends GridSimulation {
  static CellType = {
    EMPTY: new Float32Array([0, 0, 0, 0]),
    SAND: new Float32Array([1, 0, 0, 0]),
    WATER: new Float32Array([2, 0, 0, 0]),
    STONE: new Float32Array([3, 0, 0, 0]),
  };

  constructor(options) {
    super({
      ...options,
      cellTypes: StarterSimulation.CellType,
    });
  }
}
